import axios from "axios";
import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";

const Chart = ({ selectedId }) => {
  const [chartData, setChartData] = useState([]);
  const [days, setDays] = useState(1);
  const url = `https://api.coingecko.com/api/v3/coins/${selectedId}/market_chart?vs_currency=usd&days=1`;
  const fetchChart = async () => {
    const { data } = await axios.get(url);
    setChartData(data);
  };

  const daysData = [
    {
      value: 1,
      text: " 1 Hour",
    },
    {
      value: 24,
      text: " 1 Day",
    },
    {
      value: 7,
      text: " 7 Days",
    },
    {
      value: 30,
      text: " 1 Month",
    },
  ];

  console.log(chartData.prices);

  useEffect(() => {
    fetchChart();
  }, [days]);
  return (
    <div>
      {/* <Line /> */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
        }}
      >
        {daysData.map((day) => {
          return <button onClick={() => setDays(day.value)}>{day.text}</button>;
        })}
      </div>
    </div>
  );
};

export default Chart;
