import axios from "axios";
import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";

const Chart = ({ selectedId }) => {
  const [chartData, setChartData] = useState([]);
  const [days, setDays] = useState(1);
  const [loading, setLoading] = useState(false);

  const url = `https://api.coingecko.com/api/v3/coins/${selectedId}/market_chart?vs_currency=usd&days=${days}`;

  const fetchChart = async () => {
    setLoading(true);
    try {
      const { data } = await axios.get(url);
      setChartData(data);
    } catch (error) {
      // Handle the error here
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  };

  const daysData = [
    {
      value: 1,
      text: "1 Hour",
    },
    {
      value: 1,
      text: "1 Day",
    },
    {
      value: 7,
      text: "7 Days",
    },
    {
      value: 30,
      text: "1 Month",
    },
  ];

  useEffect(() => {
    fetchChart();
  }, [days, selectedId]);

  return (
    <div>
      <div>
        <Line
          data={{
            labels: chartData.map((coin) => {
              let date = new Date(coin[0]);
              let time =
                date.getHours() > 12
                  ? `${date.getHours()}-12:${date.getMinutes()} PM`
                  : `${date.getHours()} : ${date.getMinutes()}`;

              return days === 1 ? time : date;
            }),
            datasets: chartData.map((coin) => coin[1]),
          }}
        />
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
        }}
      >
        {daysData.map((day) => (
          <button
            key={day.value}
            onClick={() => setDays(day.value)}
            style={{
              backgroundColor: days === day.value ? "blue" : "transparent",
              color: days === day.value ? "white" : "black",
            }}
          >
            {day.text}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Chart;
