import { useEffect, useState } from "react";
import axios from "axios";
import Coin from "./component/Coin";
import Nav from "./component/Nav";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import CoinDetails from "./component/CoinDetails";

function App() {
  const url =
    "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false&locale=en";
  const [coins, setCoins] = useState([]);
  const [originalCoins, setOriginalCoins] = useState([]);
  const [showSearchBox, setShoiwSearchBox] = useState(true);

  useEffect(() => {
    axios
      .get(url)
      .then((response) => {
        setCoins(response.data);
        setOriginalCoins(response.data); // Store a copy of the original data
      })
      .catch((error) => {
        console.log(error);
        // Handle the error
      });
  }, []);

  function handleSearch(e) {
    const searchInput = e.target.value.toLowerCase();

    if (searchInput === "") {
      // Reset to the original coins data when the search input is empty
      setCoins(originalCoins);
    } else {
      const filteredSearch = originalCoins.filter((coin) =>
        coin.id.toLowerCase().includes(searchInput)
      );

      setCoins(filteredSearch);
    }
  }

  return (
    <div className="App">
      <Nav handleSearch={handleSearch} />
      <Router>
        <Routes>
          <Route path="/" element={<Coin coins={coins} />} />
          <Route path="/coin/:id" element={<CoinDetails coins={coins} />} />
          <Route path="*" element={<h1>Page not found</h1>} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
