import { useEffect, useState } from "react";
import axios from "axios";
import Coin from "./component/Coin";
import Nav from "./component/Nav";
import {
  BrowserRouter as Router,
  Route,
  Link,
  Outlet,
  Routes,
} from "react-router-dom";
import CoinDetails from "./component/CoinDetails";

function App() {
  const url =
    "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false&locale=en";
  const [coins, setCoins] = useState([]);

  useEffect(() => {
    axios
      .get(url)
      .then((response) => {
        setCoins(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  function handleSearch(e) {
    const serachInput = e.target.value.toLowerCase();
    if (coins.length > 1) {
      const filteredSearch = coins.filter((coin) =>
        coin.id.toLowerCase().includes(serachInput)
      );
    }
  }

  return (
    <div className="App">
      <Nav />
      <Router>
        <Routes>
          <Route path="/" element={<Coin coins={coins} />} />
          <Route path="/coin/:id" element={<CoinDetails coins={coins} />} />
          <Route path="*" element={<h1>Page not found</h1>} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
